const form=document.getElementById("form"),inputName=document.getElementById("inputName"),inputEmail=document.getElementById("inputEmail"),buttonSend=document.getElementById("buttonSend"),text=document.getElementById("text"),menuList=document.getElementById("menu"),counter=document.getElementById("counter"),inputNamePlaceholder=inputName.getAttribute("placeholder"),inputEmailPlaceholder=inputEmail.getAttribute("placeholder"),maxLength=parseInt(text.maxLength);function clearPlaceholder(e){return()=>e.placeholder=""}function setPlaceholder(e,t){return()=>e.placeholder=t}function setCounter(){counter.innerHTML=maxLength-text.value.length}function checkInput(e){0!==inputName.value.length&&0!==inputEmail.value.length||e.preventDefault()}counter.innerHTML=maxLength,inputName.addEventListener("focus",clearPlaceholder(inputName)),inputName.addEventListener("blur",setPlaceholder(inputName,inputNamePlaceholder)),inputEmail.addEventListener("focus",clearPlaceholder(inputEmail)),inputEmail.addEventListener("blur",setPlaceholder(inputEmail,inputEmailPlaceholder)),text.addEventListener("input",setCounter),form.addEventListener("submit",checkInput);const menu=[{title:"HOME",text:".header"},{title:"ABOUT US",text:".about"},{title:"PORTFOLIO",text:".portfolio"},{title:"CONTACT",text:".contact"}];function getText(e){if(e.target.closest(".menu__link"))return e.target.textContent}function findElement(e,t){return e.find((e=>e.title===t))}function scroll(e){document.querySelector(`${e.text}`).scrollIntoView({block:"center",inline:"nearest",behavior:"smooth"})}menuList.addEventListener("click",(e=>{e.preventDefault(),scroll(findElement(menu,getText(e)))}));